dataConfig.script:
function namespace(row)        {
  var namespaceMap = {
    0: "Page",
    1: "Talk",
    2: "User",
    3: "User talk",
    4: "Project",
    5: "Project talk",
    6: "File",
    7: "File talk",
    8: "Mediawiki",
    9: "Mediawiki talk",
    10: "Template",
    11: "Template talk",
    12: "Help",
    13: "Help talk",
    14: "Category",
    15: "Category talk",
  };
  var namespaceId = row.get("namespace");
  var namespaceName = namespaceMap[namespaceId];
  row.put("namespace_s", namespaceName);
  return row;
}
